                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic07 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic08 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic09 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic10 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic11 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic12 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic13 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic14 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic15 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic16 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic17 +
                            // theCurrentNodeUpdated.postMoveEvalRecord.heuristic18 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                        )
                })
        })
    );

    If(gblPF_DEBUG_EVAL,
        With(
            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_100(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " centipawn: " & theCurrentNode.postMoveEvalRecord.centipawn
            })
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fCE_100(): exiting"})
)

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_200(): entered pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_200(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theCurrentNode.preMoveFEN) &
                " captureSquareOccupier: " & theCurrentNode.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        Patch(colPF_MOVE_TREE, First(Filter(colPF_MOVE_TREE, nodeID=pNodeID)), {
            postMoveEvalRecord: 
                Patch(EVAL_RECORD_PF, {
                    heuristic11:
                        // the dumb bully part
                        Sum(
                            ForAll(
                                Split(UDF_FEN_TO_PIECE_PLACEMENT(theCurrentNode.postMoveFEN), "") As thePiece,

                                UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                            ),
                            Value
                        ) -
                        Sum(
                            ForAll(
                                Split(UDF_FEN_TO_PIECE_PLACEMENT(theCurrentNode.preMoveFEN), "") As thePiece,

                                UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                            ),
                            Value
                        ),
                    heuristic19:
                        // add a touch of randomness
                        RandBetween(-5,5)
                })
        });

        UpdateIf(colPF_MOVE_TREE, nodeID=pNodeID, {
            postMoveEvalRecord: 
                Patch(LookUp(colPF_MOVE_TREE, nodeID=pNodeID).postMoveEvalRecord, {
                    centipawn:
                        With(
                            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNodeUpdated,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic11 Basic Material BONUS/PENALTY sub-total: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic11
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic19 the God of Randomness BONUS/PENALTY sub-total: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                                })
                            );
                            Sum(
                                theCurrentNodeUpdated.postMoveEvalRecord.heuristic11,
                                theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                            )
                        )
                })
        })
    );

    If(gblPF_DEBUG_EVAL,
        With(
            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_200(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " centipawn: " & theCurrentNode.postMoveEvalRecord.centipawn
            })
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fCE_200(): exiting"})
)

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fME_EvaluateMove(): entered pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fME_EvaluateMove(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theCurrentNode.preMoveFEN) &
                " captureSquareOccupier: " & theCurrentNode.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        // determine which player's turn it is, and then see what their computer setting is currently set to
        With(
            {
                selectedEngine: If(UDF_FEN_TO_ACTIVE_COLOR(theCurrentNode.preMoveFEN)=WHITE, drpWhitePiecesCE.Selected.Value, drpBlackPiecesCE.Selected.Value)
            } As theEngine,

            If(gblPF_DEBUG_TIMERS,
                If(IsBlank(LookUp(colPF_Timers, timerName="comChessEngineFunctions.fME_EvaluateMove")),
                    Collect(colPF_Timers, {timerName:"comChessEngineFunctions.fME_EvaluateMove", startTime: Now(), stopTime: Blank() }),
                    UpdateIf(colPF_Timers, timerName="comChessEngineFunctions.fME_EvaluateMove", { startTime: Now(), stopTime: Blank() })
                )
            );

            Switch(true,
                theEngine.selectedEngine=CE_PF_200,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_200(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_200(pNodeID),
                theEngine.selectedEngine=CE_PF_300,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_300(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_300(pNodeID),
                theEngine.selectedEngine=CE_PF_400,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_400(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_400(pNodeID),
                theEngine.selectedEngine=CE_PF_500,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_500(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_500(pNodeID),
                theEngine.selectedEngine=CE_PF_600,
                    comChessEngineFunctions.fCE_600_preEvaluate();
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_600(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_600(pNodeID);
                    comChessEngineFunctions.fCE_600_postEvaluate(),
                theEngine.selectedEngine=CE_PF_700,
                    comChessEngineFunctions.fCE_700_preEvaluate();
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_700(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_700(pNodeID);
                    comChessEngineFunctions.fCE_700_postEvaluate(),
                theEngine.selectedEngine=CE_PF_800,
                    comChessEngineFunctions.fCE_800_preEvaluate();
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_800(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_800(pNodeID);
                    comChessEngineFunctions.fCE_800_postEvaluate(),
                theEngine.selectedEngine=CE_PF_900,
                    comChessEngineFunctions.fCE_900_preEvaluate();
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                            " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_900(" & pNodeID & ")"
                        })
                    );
                    comChessEngineFunctions.fCE_900(pNodeID);
                    comChessEngineFunctions.fCE_900_postEvaluate(),

                // default
                If(gblPF_DEBUG_EVAL,
                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                        " fME_EvaluateMove(): calling comChessEngineFunctions.fCE_100(" & pNodeID & ")"
                    })
                );
                comChessEngineFunctions.fCE_100(pNodeID)
            );

            If(gblPF_DEBUG_TIMERS,
                UpdateIf(colPF_Timers, timerName="comChessEngineFunctions.fME_EvaluateMove", { stopTime: Now() });
                With(
                    LookUp(colPF_Timers, timerName="comChessEngineFunctions.fME_EvaluateMove") As theTimer,

                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                        " comChessEngineFunctions.fME_EvaluateMove(): elapsed time " &
                        Text(DateDiff(theTimer.startTime, theTimer.stopTime, TimeUnit.Milliseconds)/1000) & " seconds" &
                        " selectedEngine: " & theEngine.selectedEngine

                    })
                )
            )
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fME_EvaluateMove(): exiting"})
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fME_getGamePhase(): entered pFEN: " & pFEN
    })
);

With (
    {
        board:  UDF_FEN_TO_BOARD(pFEN),
        ppd:    UDF_FEN_TO_PIECE_PLACEMENT(pFEN)
    } As theBoard,

    With (
        {
            whiteTotalDevelopment: Sum(
                                    If(UDF_getBitAtSqNum(theBoard.board,51)="P",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,52)="P",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,56)="R",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,57)="N",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,58)="B",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,59)="Q",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,60)="K",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,61)="B",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,62)="N",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,63)="R",0,1)
                                ),
            blackTotalDevelopment: Sum(
                                    If(UDF_getBitAtSqNum(theBoard.board,11)="p",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,12)="p",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,0)="r",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,1)="n",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,2)="b",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,3)="q",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,4)="k",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,5)="b",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,6)="n",0,1),
                                    If(UDF_getBitAtSqNum(theBoard.board,7)="r",0,1)
                                ),
            whiteTotalMaterial: Sum(
                                    ForAll(
                                        Split(theBoard.ppd, "") As thePiece,

                                        UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNP")
                                    ),
                                    Value
                                ),
            blackTotalMaterial: Abs(
                                    Sum(
                                        ForAll(
                                            Split(theBoard.ppd, "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "qrbnp")
                                        ),
                                        Value
                                    )
                                )
        } As theMaterial,

        If(UDF_FEN_TO_ACTIVE_COLOR(pFEN)=WHITE,
            // THEN
            Switch(true,
                Min(theMaterial.whiteTotalMaterial, theMaterial.blackTotalMaterial)>=3000 && 
                theMaterial.whiteTotalDevelopment<5 &&
                UDF_FEN_TO_FULLMOVE_COUNT(pFEN)<=10,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                            " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_OPENING"
                        })
                    );
                    GAME_PHASE_OPENING,
                Min(theMaterial.whiteTotalMaterial, theMaterial.blackTotalMaterial)>=1700,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                            " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_MIDDLE"
                        })
                    );
                    GAME_PHASE_MIDDLE,

                // default
                If(gblPF_DEBUG_EVAL,
                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                        " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_END"
                    })
                );
                GAME_PHASE_END
            ),

            // ELSE
            Switch(true,
                Min(theMaterial.whiteTotalMaterial, theMaterial.blackTotalMaterial)>=3000 && 
                theMaterial.blackTotalDevelopment<5 &&
                UDF_FEN_TO_FULLMOVE_COUNT(pFEN)<=10,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                            " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_OPENING"
                        })
                    );
                    GAME_PHASE_OPENING,
                Min(theMaterial.whiteTotalMaterial, theMaterial.blackTotalMaterial)>=1700,
                    If(gblPF_DEBUG_EVAL,
                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                            " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_MIDDLE"
                        })
                    );
                    GAME_PHASE_MIDDLE,

                // default
                If(gblPF_DEBUG_EVAL,
                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                        " comChessEngineFunctions.fME_getGamePhase(): GAME_PHASE_END"
                    })
                );
                GAME_PHASE_END
            )
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fME_END_GAME(): entered pVersion: " & pVersion &
        " pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fME_END_GAME(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theCurrentNode.preMoveFEN) &
                " captureSquareOccupier: " & theCurrentNode.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        Sum(0,
            With (
                {
                    ppd:    UDF_FEN_TO_PIECE_PLACEMENT(theCurrentNode.postMoveFEN),
                    filterScreen: If(UDF_FEN_TO_INACTIVE_COLOR(theCurrentNode.postMoveFEN)=WHITE,"QRBNP","qrbnp")
                } As localVars,

                With(
                    {
                        totalOpponentMaterial: Sum(
                                                ForAll(
                                                    Split(localVars.ppd, "") As thePiece,

                                                    UDF_getPieceCentipawnMaterialValue(thePiece.Value, localVars.filterScreen)
                                                ),
                                                Value
                                            )
                    } As localVars,

                    If(localVars.totalOpponentMaterial<>0,
                        // THEN
                        0,

                        // ELSE - we are playing against king only
                        Sum(0,
                            If(theCurrentNode.inactiveColorMoveRecord.sourceSquareOccupier in "P", 25, 0)
                        )
                    )
                )
            )
        )
    )
);

// no exit logging or will mess up return code

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_600_preEvaluate(): entering"
    })
);


With(
    {
        d0Node:         LookUp(colPF_MOVE_TREE, nodeDepth=0),
        dynamicData:    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME)
    } As theData,

    UpdateIf(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME, {
        evalData:   Patch(LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME).evalData, {
                        theirHangingSquaresSet: UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_INACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        myHangingSquaresSet:    UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theData.d0Node.postMoveFEN))
                    })
    })
);

If(gblPF_DEBUG_EVAL,
    With(
        LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
            " comChessEngineFunctions.fCE_600_preEvaluate(): DEBUG" &
            " myHangingSquaresSet: " & theData.evalData.myHangingSquaresSet &
            " theirHangingSquaresSet: " & theData.evalData.theirHangingSquaresSet
        })
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_600_preEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_600_postEvaluate(): entered"
    })
);





If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_600_postEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_700(): entered pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        {
            current:    LookUp(colPF_MOVE_TREE, nodeID=pNodeID),
            parent:     LookUp(colPF_MOVE_TREE, nodeID=LookUp(colPF_MOVE_TREE, nodeID=pNodeID).parentNodeID)
        } As theNodes,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_700(): san: " & theNodes.current.inactiveColorMoveRecord.san &
                " pacn: " & theNodes.current.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN) &
                " captureSquareOccupier: " & theNodes.current.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        Patch(colPF_MOVE_TREE, First(Filter(colPF_MOVE_TREE, nodeID=pNodeID)), {
            postMoveEvalRecord: 
                Patch(EVAL_RECORD_PF, {
                    heuristic11:
                        With(
                            {
                                materialDelta:
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.postMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ) -
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.preMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ),
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture: 
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                targetSqAttacked:
                                    UDF_isSqNumAttacked(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    )
                            } As localVars,

                            If(!localVars.targetSqAttacked || (Abs(localVars.materialSource)<=Abs(localVars.materialCapture)),
                                // THEN
                                localVars.materialDelta,

                                // ELSE - discourage the move
                                If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,-25,25)
                            )
                        ),
                    heuristic12:
                        // bonus for moving - includes capturing a piece and/or running from danger
                        With(
                            {
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                sourceSqProtected:
                                    UDF_isSqNumProtected(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                sourceSqAttacked:
                                    UDF_isSqNumAttacked(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerSqNames:
                                    UDF_getAttackerSqNamesSet(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetSqProtectedByMe:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                targetSqAttackedByThem:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    )
                            } As localVars,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                    " fCE_700(): heuristic12" & 
                                    " materialSource: " & localVars.materialSource &
                                    " materialCapture: " & localVars.materialCapture &
                                    " sourceSqProtected: " & localVars.sourceSqProtected &
                                    " sourceSqAttacked: " & localVars.sourceSqAttacked &
                                    " sourceAttackerMaterial: " & localVars.sourceAttackerMaterial &
                                    " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames &
                                    " targetSqProtectedByMe: " & localVars.targetSqProtectedByMe &
                                    " targetSqAttackedByThem: " & localVars.targetSqAttackedByThem &
                                    " targetAttackerMaterial: " & localVars.targetAttackerMaterial
                                })
                            );

                            Switch(true,
                                localVars.sourceSqAttacked && localVars.materialCapture=0 && UDF_getSetSize(localVars.sourceAttackerSqNames)=1 &&
                                UDF_isSqNumAttacked(
                                    // make sure that somebody other than the source piece is attacking the attacker
                                    UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                    UDF_sqNameToSqNum(localVars.sourceAttackerSqNames),
                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                ),
                                    // USE CASE: face fear - trust another move will remove threat
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: face fear - trust another move will remove threat" &
                                            " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames
                                        })
                                    );
                                    0,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && (!localVars.targetSqAttackedByThem) && localVars.materialCapture=0,
                                    // USE CASE: running from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: running from danger" &
                                            " bonus: " & localVars.materialSource * 0.5
                                        })
                                    );
                                    localVars.materialSource * 0.5,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)<Abs(localVars.materialSource) &&
                                (
                                    (!localVars.targetSqAttackedByThem) ||
                                    Abs(localVars.sourceAttackerMaterial)<Abs(localVars.targetAttackerMaterial)
                                ),
                                    // USE CASE: walking from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: walking from danger" &
                                            " bonus: " & localVars.materialSource + localVars.sourceAttackerMaterial
                                        })
                                    );
                                    localVars.materialSource + localVars.sourceAttackerMaterial,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && localVars.materialCapture=0 &&
                                localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: switching danger - going to a protected square 
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: switching danger - going to a protected square " &
                                            " bonus: " & localVars.materialSource * 0.25
                                        })
                                    );
                                    localVars.materialSource * 0.25,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)>=Abs(localVars.materialSource),
                                    // USE CASE: facing our fears
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: facing our fears" &
                                            " bonus: " & 0
                                        })
                                    );
                                    0,

                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && (!localVars.targetSqProtectedByMe),
                                    // USE CASE: discourage moving into a bullseye
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: discourage moving into a bullseye" &
                                            " bonus: " & -localVars.materialSource
                                        })
                                    );
                                    -localVars.materialSource,
                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: bullseye move depends upon players
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: bullseye move depends upon players" &
                                            " bonus: " &
                                                If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                                    // THEN - no bullseye during the opening
                                                    0,
                                                    // ELSE
                                                    -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.15
                                                )
                                        })
                                    );
                                    If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                        // THEN - no bullseye during the opening
                                        0,
                                        // ELSE
                                        -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.15
                                    ),
                                localVars.sourceSqAttacked && (!localVars.targetSqAttackedByThem) && localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece while running" &
                                            " bonus: " & -localVars.materialCapture + localVars.materialSource
                                        })
                                    );
                                    -localVars.materialCapture + localVars.materialSource,
                                localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -localVars.materialCapture
                                        })
                                    );
                                    -localVars.materialCapture,
                                localVars.materialCapture<>0 && localVars.targetSqAttackedByThem &&
                                Abs(localVars.materialCapture)>=Abs(localVars.materialSource),
                                    // USE CASE: capture protected piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -(localVars.materialCapture + localVars.materialSource)
                                        })
                                    );
                                    -(localVars.materialCapture + localVars.materialSource),

                                // default
                                If(gblPF_DEBUG_EVAL,
                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                        " USE CASE: default" &
                                        " bonus: 0"
                                    })
                                );
                                0
                            )
                        ),

                    heuristic15:
                        If(!(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING),
                            // THEN
                            Sum(
                                // are we capturing one of their hanging pieces ?
                                If((!IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareName)) &&
                                    theNodes.current.inactiveColorMoveRecord.captureSquareName in theData.evalData.theirHangingSquaresSet,
                                    // THEN
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " fCE_700(): heuristic15 capturing a hanging piece"
                                        })
                                    );
                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,50,-50),
                                    // ELSE
                                    0
                                ),
                                // are we reducing the number of our hanging pieces ?
                                With(
                                    {
                                        myHangingSquaresSet: UDF_getHangingSquareNamesSet(theNodes.current.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN))
                                    } As v,

                                    If(UDF_getSetSize(v.myHangingSquaresSet)<UDF_getSetSize(theData.evalData.myHangingSquaresSet),
                                        // THEN
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_700(): heuristic15 reducing number of hanging pieces" & 
                                                " prev hanging set size: " & v.myHangingSquaresSet &
                                                " new hanging set size: " & theData.evalData.myHangingSquaresSet
                                            })
                                        );
                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,35,-35),
                                        // ELSE
                                        0
                                    )
                                ),
                                // look for forks
                                With(
                                    {
                                        gettingForkedPieces:   If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,BLACK_PIECES,WHITE_PIECES),
                                        forkingPieces:         If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                    } As forkPieces,

                                    With(
                                        {
                                            gettingForkedSet:   UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.gettingForkedPieces),
                                            forkingSet:         UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.forkingPieces)
                                        } As forkSets,

                                        With(
                                            {
                                                targetTargetsSet:
                                                    // get only the eligibleSqName column
                                                    UDF_createSqNamesSet(
                                                        Concat(
                                                            ShowColumns(
                                                                Filter(colPF_LEGAL_MOVES,
                                                                    sqNum=theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    pieceUpper=
                                                                        If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P",
                                                                            // THEN - maintain the case sensitivity for PAWNs
                                                                            theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                                                            // ELSE
                                                                            Upper(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier)
                                                                        ),
                                                                    eligibleSqName in forkSets.gettingForkedSet,
                                                                    UDF_getSetSize(  
                                                                        UDF_getSetIntersection(
                                                                            slideOvers,
                                                                            UDF_getSetUnion(forkSets.forkingSet,forkSets.gettingForkedSet)
                                                                        )
                                                                    )=0 // no blockers in the sliderovers (or no sliderovers at all) results in a "true"
                                                                ), eligibleSqName
                                                            ), eligibleSqName, " "
                                                        )
                                                    ),
                                                targetIsAttacked:
                                                    UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard, 
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    )
                                            } As targetData,

                                            If(gblPF_DEBUG_EVAL,
                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                    " fCE_700(): heuristic15" & 
                                                    " gettingForkedSet: " & forkSets.gettingForkedSet &
                                                    " forkingSet: " & forkSets.forkingSet &
                                                    " targetTargetsSet: " & targetData.targetTargetsSet &
                                                    " target IsAttacked?: " & targetData.targetIsAttacked
                                                })
                                            );
                                            If(targetData.targetIsAttacked,
                                                // THEN
                                                0,

                                                // ELSE
                                                With(
                                                    {
                                                        protectedTargetTargetsCount:
                                                            Sum(
                                                                ForAll(
                                                                    Split(targetData.targetTargetsSet, " ") As theTargets,

                                                                    If(
                                                                        UDF_isSqNumProtected(
                                                                            theNodes.current.postMoveBoard,
                                                                            UDF_sqNameToSqNum(theTargets.Value),
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                                        ),
                                                                        // THEN
                                                                        1,

                                                                        // ELSE
                                                                        0
                                                                    )
                                                                ), Value
                                                            )
                                                    } As counts,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_700(): DEBUG heuristic15" & 
                                                            " attackedTargetTargetsCount: " & counts.protectedTargetTargetsCount
                                                        })
                                                    );

                                                    If(UDF_getSetSize(targetData.targetTargetsSet)<=1,
                                                        // THEN - no fork
                                                        0,

                                                        // ELSE - might be a fork worth counting
                                                        If(counts.protectedTargetTargetsCount>=UDF_getSetSize(targetData.targetTargetsSet),
                                                            // THEN
                                                            With(
                                                                {
                                                                    sourceValue: 
                                                                        UDF_minMaterialCentipawnValueFromSet(
                                                                            theNodes.current.preMoveBoard, 
                                                                            theNodes.current.inactiveColorMoveRecord.sourceSquareName,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        ),
                                                                    targetValue: 
                                                                        -UDF_minMaterialCentipawnValueFromSet(
                                                                            theNodes.current.postMoveBoard, 
                                                                            targetData.targetTargetsSet,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                } As theValues,

                                                                If(gblPF_DEBUG_EVAL,
                                                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                        " fCE_700(): DEBUG heuristic15" & 
                                                                        " source: " & theNodes.current.inactiveColorMoveRecord.sourceSquareName &
                                                                        " value: " & theValues.sourceValue &
                                                                        " target: " & targetData.targetTargetsSet &
                                                                        " value: " & theValues.targetValue
                                                                    })
                                                                );
                                                                If(Abs(theValues.targetValue) < Abs(theValues.sourceValue),
                                                                    0,
                                                                    theValues.targetValue
                                                                )
                                                            ),

                                                            // ELSE
                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_700(): DEBUG heuristic15" & 
                                                                    " target: " & targetData.targetTargetsSet &
                                                                    " value: " & 
                                                                        -UDF_minMaterialCentipawnValueFromSet(
                                                                            theNodes.current.postMoveBoard, 
                                                                            targetData.targetTargetsSet,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                })
                                                            );
                                                            -UDF_minMaterialCentipawnValueFromSet(
                                                                theNodes.current.postMoveBoard, 
                                                                targetData.targetTargetsSet,
                                                                UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                )
                            ),

                            // ELSE
                            0
                        ),

                    heuristic16:
                        If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                            // THEN - still in GAME_PHASE_OPENING
                            0,

                            // ELSE - do END GAME analysis
                            With(
                                {
                                    totalBonus:
                                        Sum(
                                            // Sum: Push Pawns forward - push the pawns forward (until we have enough Qs) - is this a pawn move ?
                                            If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_countPiecesOnBoard(theNodes.current.preMoveBoard, "Q")<3,
                                                // THEN - push the pawn and bump up the promotion to a Q
                                                If(
                                                    (IsBlank(theNodes.current.inactiveColorMoveRecord.promoteTo) || theNodes.current.inactiveColorMoveRecord.promoteTo in "Q") &&
                                                    !UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard,
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    ),
                                                    // THEN
                                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                        UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName),
                                                        8 - UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName)
                                                     ) * RandBetween(8,21) + If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0),

                                                    // ELSE
                                                    If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0)
                                                ),
                                                // ELSE
                                                If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,
                                                    RandBetween(100,200),
                                                    0
                                                )
                                            ),

                                            // Sum: trapping the king
                                            With(
                                                {
                                                    sqNum: // get the square their KING is on
                                                        UDF_SearchBoardForPiece(theNodes.current.preMoveBoard, UDF_getICPieceABBR(theNodes.current.preMoveFEN, "k"))
                                                } As theKing,

                                                If(gblPF_DEBUG_EVAL,
                                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                        " fCE_700(): DEBUG heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" 
                                                    })
                                                );

                                                With(
                                                    {
                                                        adjSquaresSet:  UDF_createSqNamesSet(UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)),
                                                        noKingPreMoveBoard: UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        noKingPostMoveBoard:    UDF_setBitAtSqNum(theNodes.current.postMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        // remove the moving piece so that we can determine if we can move to an adjacent protected square
                                                        noKingNoPiecePostMoveBoard:
                                                            UDF_setBitAtSqNum(
                                                                UDF_setBitAtSqNum(theNodes.current.postMoveBoard,
                                                                    EMPTY_FILLER_ABBR,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum
                                                                ),
                                                                EMPTY_FILLER_ABBR,
                                                                theKing.sqNum
                                                            ),
                                                        adjBlockedSquares:
                                                            UDF_occupiedSqNamesSet(
                                                                UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum), 
                                                                UDF_createSqNamesSet(
                                                                    UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)
                                                                ),
                                                                If(UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                                            )
                                                    } As nextToKing,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_700(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                            " adjSquaresSet: " & nextToKing.adjSquaresSet &
                                                            " adjSquaresSet BLOCKED: " & nextToKing.adjBlockedSquares
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " premove no king board: " & nextToKing.noKingPreMoveBoard
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " postmove no king board: " & nextToKing.noKingPostMoveBoard
                                                        });
                                                    );

                                                    With(
                                                        {
                                                            adjSquaresAttackedPreMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_700(): DEBUG heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " premove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPreMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPreMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            adjSquaresAttackedPostMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_700(): heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " postmove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPostMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPostMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            isTargetSquareProtectedByMe:
                                                                UDF_isSqNumProtected(
                                                                    nextToKing.noKingNoPiecePostMoveBoard,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                ),
                                                            numTargetSquareAttackedByThem:
                                                                If(theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet,
                                                                    // THEN
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingNoPiecePostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    ),

                                                                    // ELSE
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    )
                                                                )
                                                        } As attackData,

                                                        If(gblPF_DEBUG_EVAL,
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_700(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                                " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                " isTargetSquareProtectedByMe: " & attackData.isTargetSquareProtectedByMe &
                                                                " numTargetSquareAttackedByThem: " & attackData.numTargetSquareAttackedByThem
                                                            });
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_700(): heuristic16" &
                                                                " adj attacked or blocked: " & UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares)
                                                            });
                                                        );

                                                        With(
                                                            {
                                                                preMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPreMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                postMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                numAdjAttackedOrBlocked:
                                                                    UDF_getSetSize(UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares))
                                                            } As theKingChecked,

                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_700(): heuristic16 preMove" & 
                                                                    " # of adj Squares: " & UDF_getSetSize(nextToKing.adjSquaresSet) &
                                                                    " theKingChecked.preMove: " & theKingChecked.preMove &
                                                                    " theKingChecked.postMove: " & theKingChecked.postMove &
                                                                    " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                    " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                    " numAdjAttackedOrBlocked: " & theKingChecked.numAdjAttackedOrBlocked
                                                                })
                                                            );
                                                            Switch(true,
                                                                (UDF_getSetSize(nextToKing.adjSquaresSet) - UDF_getSetSize(attackData.adjSquaresAttackedPostMove))=1 &&
                                                                (!theKingChecked.postMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_700(): heuristic16 postmove stalemate detected "
                                                                        })
                                                                    );
                                                                    -1000,  // discourage the move
                                                                theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet &&
                                                                (
                                                                    attackData.numTargetSquareAttackedByThem>0 || // somebody can get me
                                                                    (attackData.numTargetSquareAttackedByThem=0 && (!attackData.isTargetSquareProtectedByMe)) // king can get me
                                                                ),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_700(): heuristic16 adjacent target square is not safe"
                                                                        })
                                                                    );
                                                                    -50,
                                                                attackData.numTargetSquareAttackedByThem>0,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_700(): heuristic16 target square is not safe"
                                                                        })
                                                                    );
                                                                    0,

                                                                theKingChecked.numAdjAttackedOrBlocked>=UDF_getSetSize(nextToKing.adjSquaresSet) && theKingChecked.postMove,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " checkmate detected FEN: " & theNodes.current.preMoveFEN
                                                                        })
                                                                    );
                                                                    10000, // strongly encourage the move

                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove)>UDF_getSetSize(attackData.adjSquaresAttackedPreMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " detected increase in adj squares attacked BONUS:" &
                                                                            (
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                            ) * 21
                                                                        })
                                                                    );
                                                                    (
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                    ) * 21,
                                                                // default
                                                                0
                                                            )
                                                        )
                                                    )
                                                )
                                            ),

                                            // Sum: TBD...
                                            0
                                        )
                                } As totalBonus,

                                If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,totalBonus.totalBonus,-totalBonus.totalBonus)
                            )
                        ),

                    heuristic18:
                        // add some order
                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                            // THEN
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_WHITE, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "128",
                                                    RandBetween(0,15),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(15,35)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(30,45),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(10,25),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(0,15),

                                        // default
                                        0
                                    ),

                                // default
                                0
                            ),

                            // ELSE
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_BLACK, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "456",
                                                    RandBetween(-15,0),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(-35,-15)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(-45,-30),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(-25,-10),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(-15,0),

                                        // default
                                        0
                                    ),

                                // default
                                0
                            )
                        ),
                    heuristic19:
                        RandBetween(-8,8)

                })
        });

        UpdateIf(colPF_MOVE_TREE, nodeID=pNodeID, {
            postMoveEvalRecord: 
                Patch(LookUp(colPF_MOVE_TREE, nodeID=pNodeID).postMoveEvalRecord, {
                    centipawn:
                        With(
                            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNodeUpdated,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic12: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic12
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic15: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic15
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic16: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic16
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic18: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic18
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic19 the God of Randomness BONUS/PENALTY sub-total: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                                })
                            );
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic12 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic15 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic16 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic18 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                        )
                })
        })
    );

    If(gblPF_DEBUG_EVAL,
        With(
            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_700(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " centipawn: " & theCurrentNode.postMoveEvalRecord.centipawn
            })
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fCE_700(): exiting"})
)

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_700_preEvaluate(): entering"
    })
);


With(
    {
        d0Node:         LookUp(colPF_MOVE_TREE, nodeDepth=0),
        dynamicData:    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME)
    } As theData,

    UpdateIf(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME, {
        evalData:   Patch(LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME).evalData, {
                        theirHangingSquaresSet: UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_INACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        myHangingSquaresSet:    UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        gamePhasePreMove:       comChessEngineFunctions.fME_getGamePhase(theData.d0Node.postMoveFEN) // D0 post move is D1 pre move...
                    })
    })
);

If(gblPF_DEBUG_EVAL,
    With(
        LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
            " comChessEngineFunctions.fCE_700_preEvaluate(): DEBUG" &
            " myHangingSquaresSet: " & theData.evalData.myHangingSquaresSet &
            " theirHangingSquaresSet: " & theData.evalData.theirHangingSquaresSet
        })
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_700_preEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_700_postEvaluate(): entered"
    })
);





If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_700_postEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_800(): entered pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        {
            current:    LookUp(colPF_MOVE_TREE, nodeID=pNodeID),
            parent:     LookUp(colPF_MOVE_TREE, nodeID=LookUp(colPF_MOVE_TREE, nodeID=pNodeID).parentNodeID)
        } As theNodes,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_800(): san: " & theNodes.current.inactiveColorMoveRecord.san &
                " pacn: " & theNodes.current.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN) &
                " captureSquareOccupier: " & theNodes.current.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        Patch(colPF_MOVE_TREE, First(Filter(colPF_MOVE_TREE, nodeID=pNodeID)), {
            postMoveEvalRecord: 
                Patch(EVAL_RECORD_PF, {
                    heuristic11:  // agressive-ness bonus
                        With(
                            {
                                materialDelta:
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.postMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ) -
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.preMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ),
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture: 
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                numTargetSqProtections:
                                    UDF_countSqNumProtections(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                numTargetSqAttacks:
                                    UDF_countSqNumAttacks(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                activeColor: UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                            } As localVars,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                    " fCE_800(): heuristic11" &
                                    " activeColor: " & localVars.activeColor &
                                    " materialSource: " & localVars.materialSource &
                                    " materialCapture: " & localVars.materialCapture &
                                    " moveDirection: " & theNodes.current.inactiveColorMoveRecord.moveDirection &
                                    " numTargetSqProtections: " & localVars.numTargetSqProtections &
                                    " numTargetSqAttacks: " & localVars.numTargetSqAttacks
                                })
                            );

                            Sum(
                                // discourge move back to starting position
                                Switch(true,
                                    UDF_isPieceinStartingPosition(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum
                                    ),
                                        If(localVars.activeColor=WHITE,-0.13,0.13),
                                    // default
                                    0
                                ),
                                // encourage aggressive behaviour
                                Switch(true,
                                    localVars.materialCapture=0 && localVars.numTargetSqAttacks>localVars.numTargetSqProtections,
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_800(): heuristic11" &
                                                " bonus: " & -localVars.materialSource * 0.34
                                            })
                                        );
                                        -localVars.materialSource * 0.34,
                                    localVars.activeColor=WHITE && theNodes.current.inactiveColorMoveRecord.moveDirection in "128" && 
                                    localVars.materialCapture<>0 && Abs(localVars.materialSource)<=Abs(localVars.materialCapture),
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_800(): heuristic11" &
                                                " bonus: " & localVars.materialSource * 0.34
                                            })
                                        );
                                        localVars.materialSource * 0.34,
                                    localVars.activeColor=BLACK && theNodes.current.inactiveColorMoveRecord.moveDirection in "456" && 
                                    localVars.materialCapture<>0 && Abs(localVars.materialSource)<=Abs(localVars.materialCapture),
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_800(): heuristic11" &
                                                " bonus: " & localVars.materialSource * 0.34
                                            })
                                        );
                                        localVars.materialSource * 0.34,
                                    // default
                                    0
                                )
                            )
                        ),
                    heuristic12:
                        // bonus for moving - includes capturing a piece and/or running from danger
                        With(
                            {
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                sourceSqProtected:
                                    UDF_isSqNumProtected(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                sourceSqAttacked:
                                    UDF_isSqNumAttacked(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerSqNames:
                                    UDF_getAttackerSqNamesSet(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetSqProtectedByMe:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                targetSqAttackedByThem:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    )
                            } As localVars,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                    " fCE_800(): heuristic12" & 
                                    " materialSource: " & localVars.materialSource &
                                    " materialCapture: " & localVars.materialCapture &
                                    " sourceSqProtected: " & localVars.sourceSqProtected &
                                    " sourceSqAttacked: " & localVars.sourceSqAttacked &
                                    " sourceAttackerMaterial: " & localVars.sourceAttackerMaterial &
                                    " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames &
                                    " targetSqProtectedByMe: " & localVars.targetSqProtectedByMe &
                                    " targetSqAttackedByThem: " & localVars.targetSqAttackedByThem &
                                    " targetAttackerMaterial: " & localVars.targetAttackerMaterial
                                })
                            );

                            Switch(true,
                                localVars.sourceSqAttacked && localVars.materialCapture=0 && UDF_getSetSize(localVars.sourceAttackerSqNames)=1 &&
                                UDF_isSqNumAttacked(
                                    // make sure that somebody other than the source piece is attacking the attacker
                                    UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                    UDF_sqNameToSqNum(localVars.sourceAttackerSqNames),
                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                ) &&
                                (!UDF_isSqNumProtected(
                                    // make sure that somebody other than the source piece is attacking the attacker
                                    UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                    UDF_sqNameToSqNum(localVars.sourceAttackerSqNames),
                                    UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                )),
                                    // USE CASE: face fear - trust another move will remove threat
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: face fear - trust another move will remove threat" &
                                            " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames
                                        })
                                    );
                                    0,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && (!localVars.targetSqAttackedByThem) && localVars.materialCapture=0,
                                    // USE CASE: running from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: running from danger" &
                                            " bonus: " & localVars.materialSource * 0.89
                                        })
                                    );
                                    localVars.materialSource * 0.89,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)<Abs(localVars.materialSource) &&
                                (
                                    (!localVars.targetSqAttackedByThem) ||
                                    Abs(localVars.materialSource)<Abs(localVars.targetAttackerMaterial)
                                ),
                                    // USE CASE: walking from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: walking from danger" &
                                            " bonus: " & localVars.materialSource + localVars.materialSource
                                        })
                                    );
                                    localVars.materialSource + localVars.materialSource,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && localVars.materialCapture=0 &&
                                localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: switching danger - going to a protected square 
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: switching danger - going to a protected square " &
                                            " bonus: " & localVars.materialSource * 0.25
                                        })
                                    );
                                    localVars.materialSource * 0.25,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)>=Abs(localVars.materialSource),
                                    // USE CASE: facing our fears
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: facing our fears" &
                                            " bonus: " & 0
                                        })
                                    );
                                    0,

                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && (!localVars.targetSqProtectedByMe),
                                    // USE CASE: discourage moving into a bullseye
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: discourage moving into a bullseye" &
                                            " bonus: " & -localVars.materialSource
                                        })
                                    );
                                    -localVars.materialSource,
                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: bullseye move depends upon players
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: bullseye move depends upon players" &
                                            " bonus: " &
                                                If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                                    // THEN - no bullseye during the opening
                                                    0,
                                                    // ELSE
                                                    -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.05
                                                )
                                        })
                                    );
                                    If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                        // THEN - no bullseye during the opening
                                        0,
                                        // ELSE
                                        -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.05
                                    ),
                                localVars.sourceSqAttacked && (!localVars.targetSqAttackedByThem) && localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece while running" &
                                            " bonus: " & -localVars.materialCapture + localVars.materialSource
                                        })
                                    );
                                    -localVars.materialCapture + localVars.materialSource,
                                localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -localVars.materialCapture
                                        })
                                    );
                                    -localVars.materialCapture,
                                localVars.materialCapture<>0 && localVars.targetSqAttackedByThem &&
                                Abs(localVars.materialCapture)>=Abs(localVars.materialSource),
                                    // USE CASE: capture protected piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -(localVars.materialCapture + localVars.materialSource)
                                        })
                                    );
                                    -(localVars.materialCapture + localVars.materialSource),

                                // default
                                If(gblPF_DEBUG_EVAL,
                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                        " USE CASE: default" &
                                        " bonus: 0"
                                    })
                                );
                                0
                            )
                        ),

                    heuristic15:
                        If(!(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING),
                            // THEN
                            Sum(
                                // are we capturing one of their hanging pieces ?
                                If((!IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareName)) &&
                                    theNodes.current.inactiveColorMoveRecord.captureSquareName in theData.evalData.theirHangingSquaresSet,
                                    // THEN
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " fCE_800(): heuristic15 capturing a hanging piece"
                                        })
                                    );
                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,50,-50),
                                    // ELSE
                                    0
                                ),
                                // are we reducing the number of our hanging pieces ?
                                With(
                                    {
                                        myHangingSquaresSet: UDF_getHangingSquareNamesSet(theNodes.current.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN))
                                    } As v,

                                    If(UDF_getSetSize(v.myHangingSquaresSet)<UDF_getSetSize(theData.evalData.myHangingSquaresSet),
                                        // THEN
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_800(): heuristic15 reducing number of hanging pieces" & 
                                                " prev hanging set size: " & v.myHangingSquaresSet &
                                                " new hanging set size: " & theData.evalData.myHangingSquaresSet
                                            })
                                        );
                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,35,-35),
                                        // ELSE
                                        0
                                    )
                                ),
                                // look for forks
                                With(
                                    {
                                        gettingForkedPieces:   If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,BLACK_PIECES,WHITE_PIECES),
                                        forkingPieces:         If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                    } As forkPieces,

                                    With(
                                        {
                                            gettingForkedSet:   UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.gettingForkedPieces),
                                            forkingSet:         UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.forkingPieces)
                                        } As forkSets,

                                        With(
                                            {
                                                targetTargetsSet:
                                                    // get only the eligibleSqName column
                                                    UDF_createSqNamesSet(
                                                        Concat(
                                                            ShowColumns(
                                                                Filter(colPF_LEGAL_MOVES,
                                                                    sqNum=theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    pieceUpper=
                                                                        If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P",
                                                                            // THEN - maintain the case sensitivity for PAWNs
                                                                            theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                                                            // ELSE
                                                                            Upper(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier)
                                                                        ),
                                                                    eligibleSqName in forkSets.gettingForkedSet,
                                                                    UDF_getSetSize(  
                                                                        UDF_getSetIntersection(
                                                                            slideOvers,
                                                                            UDF_getSetUnion(forkSets.forkingSet,forkSets.gettingForkedSet)
                                                                        )
                                                                    )=0 // no blockers in the sliderovers (or no sliderovers at all) results in a "true"
                                                                ), eligibleSqName
                                                            ), eligibleSqName, " "
                                                        )
                                                    ),
                                                targetIsAttacked:
                                                    UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard, 
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    ),
                                                targetIsProtected:
                                                    UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard, 
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    )
                                            } As targetData,

                                            If(gblPF_DEBUG_EVAL,
                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                    " fCE_800(): heuristic15" & 
                                                    " gettingForkedSet: " & forkSets.gettingForkedSet &
                                                    " forkingSet: " & forkSets.forkingSet &
                                                    " targetTargetsSet: " & targetData.targetTargetsSet &
                                                    " target IsAttacked?: " & targetData.targetIsAttacked
                                                })
                                            );
                                            If(targetData.targetIsAttacked && (!targetData.targetIsProtected),
                                                // THEN
                                                0,

                                                // ELSE
                                                With(
                                                    {
                                                        protectedTargetTargetsCount:
                                                            Sum(
                                                                ForAll(
                                                                    Split(targetData.targetTargetsSet, " ") As theTargets,

                                                                    If(
                                                                        UDF_isSqNumProtected(
                                                                            theNodes.current.postMoveBoard,
                                                                            UDF_sqNameToSqNum(theTargets.Value),
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                                        ),
                                                                        // THEN
                                                                        1,

                                                                        // ELSE
                                                                        0
                                                                    )
                                                                ), Value
                                                            )
                                                    } As counts,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_800(): DEBUG heuristic15" & 
                                                            " protectedTargetTargetsCount: " & counts.protectedTargetTargetsCount
                                                        })
                                                    );

                                                    If(UDF_getSetSize(targetData.targetTargetsSet)<=1,
                                                        // THEN - no fork
                                                        0,

                                                        // ELSE - might be a fork worth counting
                                                        With(
                                                            {
                                                                sourceValue: 
                                                                    UDF_minMaterialCentipawnValueFromSet(
                                                                        theNodes.current.preMoveBoard, 
                                                                        theNodes.current.inactiveColorMoveRecord.sourceSquareName,
                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                    ),
                                                                targetValue: 
                                                                    -UDF_minMaterialCentipawnValueFromSet(
                                                                        theNodes.current.postMoveBoard, 
                                                                        targetData.targetTargetsSet,
                                                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                    )
                                                            } As theValues,

                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_800(): DEBUG heuristic15" & 
                                                                    " source: " & theNodes.current.inactiveColorMoveRecord.sourceSquareName &
                                                                    " value: " & theValues.sourceValue &
                                                                    " target: " & targetData.targetTargetsSet &
                                                                    " value: " & theValues.targetValue
                                                                })
                                                            );
                                                            If(Abs(theValues.targetValue) < Abs(theValues.sourceValue),
                                                                0,
                                                                theValues.targetValue * If(targetData.targetIsAttacked,0.5,0.75)
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                ),
                                // look for skewers
                                Switch(true,
                                    theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "R",
                                        Sum(
                                            ForAll(
                                                Sequence(4, 1, 2) As theDir,
                                                With(
                                                    {
                                                        bits:   UDF_getBitsAtSqNum(
                                                                    theNodes.current.postMoveBoard, 
                                                                    UDF_getSqNumRelativeToSqNum(
                                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                        UDF_getDirX(theDir.Value),
                                                                        UDF_getDirY(theDir.Value)
                                                                    ), 
                                                                    theDir.Value, 
                                                                    7
                                                                )
                                                    } As v,
                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_800(): heuristic15 looking for a skewer" &
                                                            " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                            " v.bits: " & v.bits &
                                                            " theDir: " & theDir.Value
                                                        })
                                                    );
                                                    Switch(true,
                                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                            // THEN
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}k#{0,6}q",
                                                                MatchOptions.BeginsWith
                                                            ),
                                                            // ELSE
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}K#{0,6}Q",
                                                                MatchOptions.BeginsWith
                                                            )
                                                        ),
                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_800(): heuristic15 skewer detected" &
                                                                    " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                                    " v.bits: " & v.bits &
                                                                    " theDir: " & theDir.Value
                                                                })
                                                            );
                                                            If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,350,-350),
                                                        // default
                                                        0
                                                    )
                                                )
                                            ), Value
                                        ),
                                    theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "B",
                                        Sum(
                                            ForAll(
                                                Sequence(4, 2, 2) As theDir,
                                                With(
                                                    {
                                                        bits:   UDF_getBitsAtSqNum(
                                                                    theNodes.current.postMoveBoard, 
                                                                    UDF_getSqNumRelativeToSqNum(
                                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                        UDF_getDirX(theDir.Value),
                                                                        UDF_getDirY(theDir.Value)
                                                                    ), 
                                                                    theDir.Value, 
                                                                    7
                                                                )
                                                    } As v,
                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_800(): heuristic15 looking for a skewer" &
                                                            " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                            " v.bits: " & v.bits &
                                                            " theDir: " & theDir.Value
                                                        })
                                                    );
                                                    Switch(true,
                                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                            // THEN
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}k#{0,6}[qr]",
                                                                MatchOptions.BeginsWith
                                                            ),
                                                            // ELSE
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}K#{0,6}[QR]",
                                                                MatchOptions.BeginsWith
                                                            )
                                                        ),
                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_800(): heuristic15 skewer detected" &
                                                                    " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                                    " v.bits: " & v.bits &
                                                                    " theDir: " & theDir.Value
                                                                })
                                                            );
                                                            If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,250,-250),
                                                        // default
                                                        0
                                                    )
                                                )
                                            ), Value
                                        ),
                                    // default
                                    0
                                )
                            ),

                            // ELSE
                            0
                        ),

                    heuristic16:
                        If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                            // THEN - still in GAME_PHASE_OPENING
                            0,

                            // ELSE - do END GAME analysis
                            With(
                                {
                                    totalBonus:
                                        Sum(
                                            // Sum: Push Pawns forward - push the pawns forward (until we have enough Qs) - is this a pawn move ?
                                            If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_countPiecesOnBoard(theNodes.current.preMoveBoard, "Q")<3,
                                                // THEN - push the pawn and bump up the promotion to a Q
                                                If(
                                                    (IsBlank(theNodes.current.inactiveColorMoveRecord.promoteTo) || theNodes.current.inactiveColorMoveRecord.promoteTo in "Q") &&
                                                    !UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard,
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    ),
                                                    // THEN
                                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                        UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName),
                                                        8 - UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName)
                                                     ) * RandBetween(8,21) + If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0),

                                                    // ELSE
                                                    If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0)
                                                ),
                                                // ELSE
                                                If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,
                                                    RandBetween(100,200),
                                                    0
                                                )
                                            ),

                                            // Sum: trapping the king
                                            With(
                                                {
                                                    sqNum: // get the square their KING is on
                                                        UDF_SearchBoardForPiece(theNodes.current.preMoveBoard, UDF_getICPieceABBR(theNodes.current.preMoveFEN, "k"))
                                                } As theKing,

                                                If(gblPF_DEBUG_EVAL,
                                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                        " fCE_800(): DEBUG heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" 
                                                    })
                                                );

                                                With(
                                                    {
                                                        adjSquaresSet:  UDF_createSqNamesSet(UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)),
                                                        noKingPreMoveBoard: UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        noKingPostMoveBoard:    UDF_setBitAtSqNum(theNodes.current.postMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        // remove the moving piece so that we can determine if we can move to an adjacent protected square
                                                        noKingNoPiecePostMoveBoard:
                                                            UDF_setBitAtSqNum(
                                                                UDF_setBitAtSqNum(theNodes.current.postMoveBoard,
                                                                    EMPTY_FILLER_ABBR,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum
                                                                ),
                                                                EMPTY_FILLER_ABBR,
                                                                theKing.sqNum
                                                            ),
                                                        adjBlockedSquares:
                                                            UDF_occupiedSqNamesSet(
                                                                UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum), 
                                                                UDF_createSqNamesSet(
                                                                    UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)
                                                                ),
                                                                If(UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                                            )
                                                    } As nextToKing,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_800(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                            " adjSquaresSet: " & nextToKing.adjSquaresSet &
                                                            " adjSquaresSet BLOCKED: " & nextToKing.adjBlockedSquares
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " premove no king board: " & nextToKing.noKingPreMoveBoard
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " postmove no king board: " & nextToKing.noKingPostMoveBoard
                                                        });
                                                    );

                                                    With(
                                                        {
                                                            adjSquaresAttackedPreMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_800(): DEBUG heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " premove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPreMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPreMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            adjSquaresAttackedPostMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_800(): heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " postmove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPostMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPostMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            isTargetSquareProtectedByMe:
                                                                UDF_isSqNumProtected(
                                                                    nextToKing.noKingNoPiecePostMoveBoard,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                ),
                                                            numTargetSquareAttackedByThem:
                                                                If(theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet,
                                                                    // THEN
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingNoPiecePostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    ),

                                                                    // ELSE
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    )
                                                                )
                                                        } As attackData,

                                                        If(gblPF_DEBUG_EVAL,
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_800(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                                " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                " isTargetSquareProtectedByMe: " & attackData.isTargetSquareProtectedByMe &
                                                                " numTargetSquareAttackedByThem: " & attackData.numTargetSquareAttackedByThem
                                                            });
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_800(): heuristic16" &
                                                                " adj attacked or blocked: " & UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares)
                                                            });
                                                        );

                                                        With(
                                                            {
                                                                preMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPreMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                postMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                numAdjAttackedOrBlocked:
                                                                    UDF_getSetSize(UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares))
                                                            } As theKingChecked,

                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_800(): heuristic16 preMove" & 
                                                                    " # of adj Squares: " & UDF_getSetSize(nextToKing.adjSquaresSet) &
                                                                    " theKingChecked.preMove: " & theKingChecked.preMove &
                                                                    " theKingChecked.postMove: " & theKingChecked.postMove &
                                                                    " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                    " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                    " numAdjAttackedOrBlocked: " & theKingChecked.numAdjAttackedOrBlocked
                                                                })
                                                            );
                                                            Switch(true,
                                                                (UDF_getSetSize(nextToKing.adjSquaresSet) - UDF_getSetSize(attackData.adjSquaresAttackedPostMove))=1 &&
                                                                (!theKingChecked.postMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_800(): heuristic16 postmove stalemate detected "
                                                                        })
                                                                    );
                                                                    -1000,  // discourage the move
                                                                theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet &&
                                                                (
                                                                    attackData.numTargetSquareAttackedByThem>0 || // somebody can get me
                                                                    (attackData.numTargetSquareAttackedByThem=0 && (!attackData.isTargetSquareProtectedByMe)) // king can get me
                                                                ),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_800(): heuristic16 adjacent target square is not safe"
                                                                        })
                                                                    );
                                                                    -50,
                                                                attackData.numTargetSquareAttackedByThem>0,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_800(): heuristic16 target square is not safe"
                                                                        })
                                                                    );
                                                                    0,

                                                                theKingChecked.numAdjAttackedOrBlocked>=UDF_getSetSize(nextToKing.adjSquaresSet) && theKingChecked.postMove,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " checkmate detected FEN: " & theNodes.current.preMoveFEN
                                                                        })
                                                                    );
                                                                    10000, // strongly encourage the move

                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove)>UDF_getSetSize(attackData.adjSquaresAttackedPreMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " detected increase in adj squares attacked BONUS:" &
                                                                            (
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                            ) * 21
                                                                        })
                                                                    );
                                                                    (
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                    ) * 21,
                                                                // default
                                                                0
                                                            )
                                                        )
                                                    )
                                                )
                                            ),

                                            // Sum: TBD...
                                            0
                                        )
                                } As totalBonus,

                                If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,totalBonus.totalBonus,-totalBonus.totalBonus)
                            )
                        ),

                    heuristic18:
                        // add some order
                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                            // THEN
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_WHITE, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "128",
                                                    RandBetween(0,15),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(15,35)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(30,45),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(10,25),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(0,15),

                                        // default
                                        0
                                    ),

                                // default
                                0
                            ),

                            // ELSE
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_BLACK, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "456",
                                                    RandBetween(-15,0),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(-35,-15)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(-45,-30),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(-25,-10),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(-15,0),

                                        // default
                                        0
                                    ),

                                // default
                                0
                            )
                        ),
                    heuristic19:
                        RandBetween(-8,8)

                })
        });

        UpdateIf(colPF_MOVE_TREE, nodeID=pNodeID, {
            postMoveEvalRecord: 
                Patch(LookUp(colPF_MOVE_TREE, nodeID=pNodeID).postMoveEvalRecord, {
                    centipawn:
                        With(
                            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNodeUpdated,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic11: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic11
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic12: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic12
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic15: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic15
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic16: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic16
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic18: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic18
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic19 the God of Randomness BONUS/PENALTY sub-total: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                                })
                            );
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic11 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic12 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic15 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic16 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic18 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                        )
                })
        })
    );

    If(gblPF_DEBUG_EVAL,
        With(
            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_800(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " centipawn: " & theCurrentNode.postMoveEvalRecord.centipawn
            })
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fCE_800(): exiting"})
)

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_800_preEvaluate(): entering"
    })
);


With(
    {
        d0Node:         LookUp(colPF_MOVE_TREE, nodeDepth=0),
        dynamicData:    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME)
    } As theData,

    UpdateIf(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME, {
        evalData:   Patch(LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME).evalData, {
                        theirHangingSquaresSet: UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_INACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        myHangingSquaresSet:    UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        gamePhasePreMove:       comChessEngineFunctions.fME_getGamePhase(theData.d0Node.postMoveFEN) // D0 post move is D1 pre move...
                    })
    })
);

If(gblPF_DEBUG_EVAL,
    With(
        LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
            " comChessEngineFunctions.fCE_800_preEvaluate(): DEBUG" &
            " myHangingSquaresSet: " & theData.evalData.myHangingSquaresSet &
            " theirHangingSquaresSet: " & theData.evalData.theirHangingSquaresSet
        })
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_800_preEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_800_postEvaluate(): entered"
    })
);





If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_800_postEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_900(): entered pNodeID: " & pNodeID
    })
);

With(
    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

    With(
        {
            current:    LookUp(colPF_MOVE_TREE, nodeID=pNodeID),
            parent:     LookUp(colPF_MOVE_TREE, nodeID=LookUp(colPF_MOVE_TREE, nodeID=pNodeID).parentNodeID)
        } As theNodes,

        If(gblPF_DEBUG_EVAL,
            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_900(): san: " & theNodes.current.inactiveColorMoveRecord.san &
                " pacn: " & theNodes.current.inactiveColorMoveRecord.pacn &
                " move color: " & UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN) &
                " captureSquareOccupier: " & theNodes.current.inactiveColorMoveRecord.captureSquareOccupier
            })
        );

        Patch(colPF_MOVE_TREE, First(Filter(colPF_MOVE_TREE, nodeID=pNodeID)), {
            postMoveEvalRecord: 
                Patch(EVAL_RECORD_PF, {
                    heuristic11:  // agressive-ness bonus
                        With(
                            {
                                materialDelta:
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.postMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ) -
                                    Sum(
                                        ForAll(
                                            Split(UDF_FEN_TO_PIECE_PLACEMENT(theNodes.current.preMoveFEN), "") As thePiece,

                                            UDF_getPieceCentipawnMaterialValue(thePiece.Value, "QRBNPqrbnp")
                                        ),
                                        Value
                                    ),
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture: 
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                numTargetSqProtections:
                                    UDF_countSqNumProtections(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                numTargetSqAttacks:
                                    UDF_countSqNumAttacks(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                activeColor: UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                            } As localVars,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                    " fCE_900(): heuristic11" &
                                    " activeColor: " & localVars.activeColor &
                                    " materialSource: " & localVars.materialSource &
                                    " materialCapture: " & localVars.materialCapture &
                                    " moveDirection: " & theNodes.current.inactiveColorMoveRecord.moveDirection &
                                    " numTargetSqProtections: " & localVars.numTargetSqProtections &
                                    " numTargetSqAttacks: " & localVars.numTargetSqAttacks
                                })
                            );

                            Sum(
                                // discourge move back to starting position
                                Switch(true,
                                    UDF_isPieceinStartingPosition(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum
                                    ),
                                        If(localVars.activeColor=WHITE,-0.13,0.13),
                                    // default
                                    0
                                ),
                                // encourage aggressive behaviour
                                Switch(true,
                                    localVars.materialCapture=0 && localVars.numTargetSqAttacks>localVars.numTargetSqProtections,
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_900(): heuristic11" &
                                                " bonus: " & -localVars.materialSource * 0.34
                                            })
                                        );
                                        -localVars.materialSource * 0.34,
                                    localVars.activeColor=WHITE && theNodes.current.inactiveColorMoveRecord.moveDirection in "128" && 
                                    localVars.materialCapture<>0 && Abs(localVars.materialSource)<=Abs(localVars.materialCapture),
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_900(): heuristic11" &
                                                " bonus: " & localVars.materialSource * 0.34
                                            })
                                        );
                                        localVars.materialSource * 0.34,
                                    localVars.activeColor=BLACK && theNodes.current.inactiveColorMoveRecord.moveDirection in "456" && 
                                    localVars.materialCapture<>0 && Abs(localVars.materialSource)<=Abs(localVars.materialCapture),
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_900(): heuristic11" &
                                                " bonus: " & localVars.materialSource * 0.34
                                            })
                                        );
                                        localVars.materialSource * 0.34,
                                    // default
                                    0
                                )
                            )
                        ),
                    heuristic12:
                        // bonus for moving - includes capturing a piece and/or running from danger
                        With(
                            {
                                materialSource:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                        ALL_PIECES
                                    ),
                                materialCapture:
                                    UDF_getPieceCentipawnMaterialValue(
                                        theNodes.current.inactiveColorMoveRecord.captureSquareOccupier,
                                        ALL_PIECES
                                    ),
                                sourceSqProtected:
                                    UDF_isSqNumProtected(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                sourceSqAttacked:
                                    UDF_isSqNumAttacked(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                sourceAttackerSqNames:
                                    UDF_getAttackerSqNamesSet(
                                        theNodes.current.preMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetSqProtectedByMe:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                    ),
                                targetSqAttackedByThem:
                                    UDF_isSqNumProtected(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    ),
                                targetAttackerMaterial:
                                    UDF_getSqNumAttackersMinMaterialCentipawn(
                                        theNodes.current.postMoveBoard,
                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                    )
                            } As localVars,

                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                    " fCE_900(): heuristic12" & 
                                    " materialSource: " & localVars.materialSource &
                                    " materialCapture: " & localVars.materialCapture &
                                    " sourceSqProtected: " & localVars.sourceSqProtected &
                                    " sourceSqAttacked: " & localVars.sourceSqAttacked &
                                    " sourceAttackerMaterial: " & localVars.sourceAttackerMaterial &
                                    " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames &
                                    " targetSqProtectedByMe: " & localVars.targetSqProtectedByMe &
                                    " targetSqAttackedByThem: " & localVars.targetSqAttackedByThem &
                                    " targetAttackerMaterial: " & localVars.targetAttackerMaterial
                                })
                            );

                            Switch(true,
                                localVars.sourceSqAttacked && localVars.materialCapture=0 && UDF_getSetSize(localVars.sourceAttackerSqNames)=1 &&
                                UDF_isSqNumAttacked(
                                    // make sure that somebody other than the source piece is attacking the attacker
                                    UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                    UDF_sqNameToSqNum(localVars.sourceAttackerSqNames),
                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                ) &&
                                (!UDF_isSqNumProtected(
                                    // make sure that somebody other than the source piece is attacking the attacker
                                    UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                    UDF_sqNameToSqNum(localVars.sourceAttackerSqNames),
                                    UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                )),
                                    // USE CASE: face fear - trust another move will remove threat
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: face fear - trust another move will remove threat" &
                                            " sourceAttackerSqNames: " & localVars.sourceAttackerSqNames
                                        })
                                    );
                                    0,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && (!localVars.targetSqAttackedByThem) && localVars.materialCapture=0,
                                    // USE CASE: running from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: running from danger" &
                                            " bonus: " & localVars.materialSource * 0.89
                                        })
                                    );
                                    localVars.materialSource * 0.89,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)<Abs(localVars.materialSource) &&
                                (
                                    (!localVars.targetSqAttackedByThem) ||
                                    Abs(localVars.materialSource)<Abs(localVars.targetAttackerMaterial)
                                ),
                                    // USE CASE: walking from danger
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: walking from danger" &
                                            " bonus: " & localVars.materialSource + localVars.materialSource
                                        })
                                    );
                                    localVars.materialSource + localVars.materialSource,

                                localVars.sourceSqAttacked && (!localVars.sourceSqProtected) && localVars.materialCapture=0 &&
                                localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: switching danger - going to a protected square 
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: switching danger - going to a protected square " &
                                            " bonus: " & localVars.materialSource * 0.25
                                        })
                                    );
                                    localVars.materialSource * 0.25,

                                localVars.sourceSqAttacked && localVars.sourceSqProtected && localVars.materialCapture=0 &&
                                Abs(localVars.sourceAttackerMaterial)>=Abs(localVars.materialSource),
                                    // USE CASE: facing our fears
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: facing our fears" &
                                            " bonus: " & 0
                                        })
                                    );
                                    0,

                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && (!localVars.targetSqProtectedByMe),
                                    // USE CASE: discourage moving into a bullseye
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: discourage moving into a bullseye" &
                                            " bonus: " & -localVars.materialSource
                                        })
                                    );
                                    -localVars.materialSource,
                                (!localVars.sourceSqAttacked) && localVars.materialCapture=0 && localVars.targetSqAttackedByThem && localVars.targetSqProtectedByMe,
                                    // USE CASE: bullseye move depends upon players
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: bullseye move depends upon players" &
                                            " bonus: " &
                                                If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                                    // THEN - no bullseye during the opening
                                                    0,
                                                    // ELSE
                                                    -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.05
                                                )
                                        })
                                    );
                                    If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                                        // THEN - no bullseye during the opening
                                        0,
                                        // ELSE
                                        -(localVars.targetAttackerMaterial + localVars.materialSource) * 0.05
                                    ),
                                localVars.sourceSqAttacked && (!localVars.targetSqAttackedByThem) && localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece while running" &
                                            " bonus: " & -localVars.materialCapture + localVars.materialSource
                                        })
                                    );
                                    -localVars.materialCapture + localVars.materialSource,
                                localVars.materialCapture<>0 && (!localVars.targetSqAttackedByThem),
                                    // USE CASE: capture hanging piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -localVars.materialCapture
                                        })
                                    );
                                    -localVars.materialCapture,
                                localVars.materialCapture<>0 && localVars.targetSqAttackedByThem &&
                                Abs(localVars.materialCapture)>=Abs(localVars.materialSource),
                                    // USE CASE: capture protected piece
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " USE CASE: capture hanging piece" &
                                            " bonus: " & -(localVars.materialCapture + localVars.materialSource)
                                        })
                                    );
                                    -(localVars.materialCapture + localVars.materialSource),

                                // default
                                If(gblPF_DEBUG_EVAL,
                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                        " USE CASE: default" &
                                        " bonus: 0"
                                    })
                                );
                                0
                            )
                        ),

                    heuristic15:
                        If(!(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING),
                            // THEN
                            Sum(
                                // are we capturing one of their hanging pieces ?
                                If((!IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareName)) &&
                                    theNodes.current.inactiveColorMoveRecord.captureSquareName in theData.evalData.theirHangingSquaresSet,
                                    // THEN
                                    If(gblPF_DEBUG_EVAL,
                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                            " fCE_900(): heuristic15 capturing a hanging piece"
                                        })
                                    );
                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,50,-50),
                                    // ELSE
                                    0
                                ),
                                // are we reducing the number of our hanging pieces ?
                                With(
                                    {
                                        myHangingSquaresSet: UDF_getHangingSquareNamesSet(theNodes.current.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN))
                                    } As v,

                                    If(UDF_getSetSize(v.myHangingSquaresSet)<UDF_getSetSize(theData.evalData.myHangingSquaresSet),
                                        // THEN
                                        If(gblPF_DEBUG_EVAL,
                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                " fCE_900(): heuristic15 reducing number of hanging pieces" & 
                                                " prev hanging set size: " & v.myHangingSquaresSet &
                                                " new hanging set size: " & theData.evalData.myHangingSquaresSet
                                            })
                                        );
                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,35,-35),
                                        // ELSE
                                        0
                                    )
                                ),
                                // look for forks
                                With(
                                    {
                                        gettingForkedPieces:   If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,BLACK_PIECES,WHITE_PIECES),
                                        forkingPieces:         If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                    } As forkPieces,

                                    With(
                                        {
                                            gettingForkedSet:   UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.gettingForkedPieces),
                                            forkingSet:         UDF_occupiedSqNamesSet(theNodes.current.postMoveBoard, UNIVERSAL_SQNAMES_SET, forkPieces.forkingPieces)
                                        } As forkSets,
                                        
                                        With(
                                            {
                                                targetTargetsSet:
                                                    // get only the eligibleSqName column
                                                    UDF_createSqNamesSet(
                                                        Concat(
                                                            ShowColumns(
                                                                Filter(colPF_LEGAL_MOVES,
                                                                    sqNum=theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    pieceUpper=
                                                                        If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P",
                                                                            // THEN - maintain the case sensitivity for PAWNs
                                                                            theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier,
                                                                            // ELSE
                                                                            Upper(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier)
                                                                        ),
                                                                    eligibleSqName in forkSets.gettingForkedSet,
                                                                    UDF_getSetSize(  
                                                                        UDF_getSetIntersection(
                                                                            slideOvers,
                                                                            UDF_getSetUnion(forkSets.forkingSet,forkSets.gettingForkedSet)
                                                                        )
                                                                    )=0 // no blockers in the sliderovers (or no sliderovers at all) results in a "true"
                                                                ), eligibleSqName
                                                            ), eligibleSqName, " "
                                                        )
                                                    ),
                                                targetIsAttacked:
                                                    UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard, 
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    ),
                                                targetIsProtected:
                                                    UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard, 
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    )
                                            } As targetData,

                                            If(gblPF_DEBUG_EVAL,
                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                    " fCE_900(): heuristic15" & 
                                                    " gettingForkedSet: " & forkSets.gettingForkedSet &
                                                    " forkingSet: " & forkSets.forkingSet &
                                                    " targetTargetsSet: " & targetData.targetTargetsSet &
                                                    " target IsAttacked?: " & targetData.targetIsAttacked
                                                })
                                            );
                                            If(targetData.targetIsAttacked && (!targetData.targetIsProtected),
                                                // THEN
                                                0,

                                                // ELSE
                                                With(
                                                    {
                                                        protectedTargetTargetsCount:
                                                            Sum(
                                                                ForAll(
                                                                    Split(targetData.targetTargetsSet, " ") As theTargets,

                                                                    If(
                                                                        UDF_isSqNumProtected(
                                                                            theNodes.current.postMoveBoard,
                                                                            UDF_sqNameToSqNum(theTargets.Value),
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                                        ),
                                                                        // THEN
                                                                        1,

                                                                        // ELSE
                                                                        0
                                                                    )
                                                                ), Value
                                                            )
                                                    } As counts,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_900(): DEBUG heuristic15" & 
                                                            " protectedTargetTargetsCount: " & counts.protectedTargetTargetsCount
                                                        })
                                                    );

                                                    If(UDF_getSetSize(targetData.targetTargetsSet)<=1,
                                                        // THEN - no fork
                                                        0,

                                                        // ELSE - might be a fork worth counting
                                                        With(
                                                            {
                                                                sourceValue: 
                                                                    UDF_minMaterialCentipawnValueFromSet(
                                                                        theNodes.current.preMoveBoard, 
                                                                        theNodes.current.inactiveColorMoveRecord.sourceSquareName,
                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                    ),
                                                                targetValue: 
                                                                    -UDF_minMaterialCentipawnValueFromSet(
                                                                        theNodes.current.postMoveBoard, 
                                                                        targetData.targetTargetsSet,
                                                                        UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                    )
                                                            } As theValues,

                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_900(): DEBUG heuristic15" & 
                                                                    " source: " & theNodes.current.inactiveColorMoveRecord.sourceSquareName &
                                                                    " value: " & theValues.sourceValue &
                                                                    " target: " & targetData.targetTargetsSet &
                                                                    " value: " & theValues.targetValue
                                                                })
                                                            );
                                                            If(Abs(theValues.targetValue) < Abs(theValues.sourceValue),
                                                                0,
                                                                theValues.targetValue * If(targetData.targetIsAttacked,0.5,0.75)
                                                            )
                                                        )
                                                    )
                                                )
                                            )
                                        )
                                    )
                                ),
                                // look for skewers
                                Switch(true,
                                    theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "R",
                                        Sum(
                                            ForAll(
                                                Sequence(4, 1, 2) As theDir,
                                                With(
                                                    {
                                                        bits:   UDF_getBitsAtSqNum(
                                                                    theNodes.current.postMoveBoard, 
                                                                    UDF_getSqNumRelativeToSqNum(
                                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                        UDF_getDirX(theDir.Value),
                                                                        UDF_getDirY(theDir.Value)
                                                                    ), 
                                                                    theDir.Value, 
                                                                    7
                                                                )
                                                    } As v,
                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_900(): heuristic15 looking for a skewer" &
                                                            " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                            " v.bits: " & v.bits &
                                                            " theDir: " & theDir.Value
                                                        })
                                                    );
                                                    Switch(true,
                                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                            // THEN
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}k#{0,6}q",
                                                                MatchOptions.BeginsWith
                                                            ),
                                                            // ELSE
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}K#{0,6}Q",
                                                                MatchOptions.BeginsWith
                                                            )
                                                        ),
                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_900(): heuristic15 skewer detected" &
                                                                    " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                                    " v.bits: " & v.bits &
                                                                    " theDir: " & theDir.Value
                                                                })
                                                            );
                                                            If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,350,-350),
                                                        // default
                                                        0
                                                    )
                                                )
                                            ), Value
                                        ),
                                    theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "B",
                                        Sum(
                                            ForAll(
                                                Sequence(4, 2, 2) As theDir,
                                                With(
                                                    {
                                                        bits:   UDF_getBitsAtSqNum(
                                                                    theNodes.current.postMoveBoard, 
                                                                    UDF_getSqNumRelativeToSqNum(
                                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                        UDF_getDirX(theDir.Value),
                                                                        UDF_getDirY(theDir.Value)
                                                                    ), 
                                                                    theDir.Value, 
                                                                    7
                                                                )
                                                    } As v,
                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_900(): heuristic15 looking for a skewer" &
                                                            " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                            " v.bits: " & v.bits &
                                                            " theDir: " & theDir.Value
                                                        })
                                                    );
                                                    Switch(true,
                                                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                            // THEN
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}k#{0,6}[qr]",
                                                                MatchOptions.BeginsWith
                                                            ),
                                                            // ELSE
                                                            IsMatch(
                                                                v.bits, 
                                                                "#{0,6}K#{0,6}[QR]",
                                                                MatchOptions.BeginsWith
                                                            )
                                                        ),
                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_900(): heuristic15 skewer detected" &
                                                                    " source piece: " & theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier &
                                                                    " v.bits: " & v.bits &
                                                                    " theDir: " & theDir.Value
                                                                })
                                                            );
                                                            If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,250,-250),
                                                        // default
                                                        0
                                                    )
                                                )
                                            ), Value
                                        ),
                                    // default
                                    0
                                )
                            ),

                            // ELSE
                            0
                        ),

                    heuristic16:
                        If(theData.evalData.gamePhasePreMove=GAME_PHASE_OPENING,
                            // THEN - still in GAME_PHASE_OPENING
                            0,

                            // ELSE - do END GAME analysis
                            With(
                                {
                                    totalBonus:
                                        Sum(
                                            // Sum: Push Pawns forward - push the pawns forward (until we have enough Qs) - is this a pawn move ?
                                            If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_countPiecesOnBoard(theNodes.current.preMoveBoard, "Q")<3,
                                                // THEN - push the pawn and bump up the promotion to a Q
                                                If(
                                                    (IsBlank(theNodes.current.inactiveColorMoveRecord.promoteTo) || theNodes.current.inactiveColorMoveRecord.promoteTo in "Q") &&
                                                    !UDF_isSqNumAttacked(
                                                        theNodes.current.postMoveBoard,
                                                        theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.postMoveFEN)
                                                    ),
                                                    // THEN
                                                    If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                                                        UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName),
                                                        8 - UDF_getRankFromSqName(theNodes.current.inactiveColorMoveRecord.targetSquareName)
                                                     ) * RandBetween(8,21) + If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0),

                                                    // ELSE
                                                    If(UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,RandBetween(100,200),0)
                                                ),
                                                // ELSE
                                                If(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "P" && UDF_FEN_TO_HALFMOVE_COUNT(theNodes.current.preMoveFEN)>10,
                                                    RandBetween(100,200),
                                                    0
                                                )
                                            ),

                                            // Sum: trapping the king
                                            With(
                                                {
                                                    sqNum: // get the square their KING is on
                                                        UDF_SearchBoardForPiece(theNodes.current.preMoveBoard, UDF_getICPieceABBR(theNodes.current.preMoveFEN, "k"))
                                                } As theKing,

                                                If(gblPF_DEBUG_EVAL,
                                                    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                        " fCE_900(): DEBUG heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" 
                                                    })
                                                );

                                                With(
                                                    {
                                                        adjSquaresSet:  UDF_createSqNamesSet(UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)),
                                                        noKingPreMoveBoard: UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        noKingPostMoveBoard:    UDF_setBitAtSqNum(theNodes.current.postMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum),
                                                        // remove the moving piece so that we can determine if we can move to an adjacent protected square
                                                        noKingNoPiecePostMoveBoard:
                                                            UDF_setBitAtSqNum(
                                                                UDF_setBitAtSqNum(theNodes.current.postMoveBoard,
                                                                    EMPTY_FILLER_ABBR,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum
                                                                ),
                                                                EMPTY_FILLER_ABBR,
                                                                theKing.sqNum
                                                            ),
                                                        adjBlockedSquares:
                                                            UDF_occupiedSqNamesSet(
                                                                UDF_setBitAtSqNum(theNodes.current.preMoveBoard,EMPTY_FILLER_ABBR,theKing.sqNum), 
                                                                UDF_createSqNamesSet(
                                                                    UDF_sqNumToAdjacentSqNamesSet(theKing.sqNum) & " " &  UDF_sqNumToSqName(theKing.sqNum)
                                                                ),
                                                                If(UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,WHITE_PIECES,BLACK_PIECES)
                                                            )
                                                    } As nextToKing,

                                                    If(gblPF_DEBUG_EVAL,
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " fCE_900(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                            " adjSquaresSet: " & nextToKing.adjSquaresSet &
                                                            " adjSquaresSet BLOCKED: " & nextToKing.adjBlockedSquares
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " premove no king board: " & nextToKing.noKingPreMoveBoard
                                                        });
                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                            " postmove no king board: " & nextToKing.noKingPostMoveBoard
                                                        });
                                                    );

                                                    With(
                                                        {
                                                            adjSquaresAttackedPreMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_900(): DEBUG heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " premove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPreMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPreMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            adjSquaresAttackedPostMove:
                                                                UDF_createSqNamesSet(
                                                                    Concat(
                                                                        ForAll(
                                                                            Split(nextToKing.adjSquaresSet, " ") As adjSqName,

                                                                            If(gblPF_DEBUG_EVAL,
                                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                                    " fCE_900(): heuristic16 adjSqName.Value: " & adjSqName.Value &
                                                                                    " postmove AttackerSet: " &
                                                                                        UDF_getAttackerSqNamesSet(
                                                                                            nextToKing.noKingPostMoveBoard,
                                                                                            UDF_sqNameToSqNum(adjSqName.Value),
                                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                        )
                                                                                })
                                                                            );

                                                                            If(
                                                                                UDF_getSetSize(
                                                                                    UDF_getAttackerSqNamesSet(
                                                                                        nextToKing.noKingPostMoveBoard,
                                                                                        UDF_sqNameToSqNum(adjSqName.Value),
                                                                                        UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                                    )
                                                                                )>0,
                                                                                adjSqName.Value, ""
                                                                            )
                                                                        ), Value, " "
                                                                    )
                                                                ),
                                                            isTargetSquareProtectedByMe:
                                                                UDF_isSqNumProtected(
                                                                    nextToKing.noKingNoPiecePostMoveBoard,
                                                                    theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                    UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                ),
                                                            numTargetSquareAttackedByThem:
                                                                If(theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet,
                                                                    // THEN
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingNoPiecePostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    ),

                                                                    // ELSE
                                                                    UDF_getSetSize(
                                                                        UDF_getAttackerSqNamesSet(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theNodes.current.inactiveColorMoveRecord.targetSquareNum,
                                                                            UDF_FEN_TO_INACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )
                                                                    )
                                                                )
                                                        } As attackData,

                                                        If(gblPF_DEBUG_EVAL,
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_900(): heuristic16 theKing.sqNum: " & UDF_sqNumToSqName(theKing.sqNum) & "(" & theKing.sqNum & ")" &
                                                                " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                " isTargetSquareProtectedByMe: " & attackData.isTargetSquareProtectedByMe &
                                                                " numTargetSquareAttackedByThem: " & attackData.numTargetSquareAttackedByThem
                                                            });
                                                            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                " fCE_900(): heuristic16" &
                                                                " adj attacked or blocked: " & UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares)
                                                            });
                                                        );

                                                        With(
                                                            {
                                                                preMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPreMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                postMove:
                                                                    If(
                                                                        UDF_countSqNumAttacks(
                                                                            nextToKing.noKingPostMoveBoard,
                                                                            theKing.sqNum,
                                                                            UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)
                                                                        )>0,
                                                                        true,false
                                                                    ),
                                                                numAdjAttackedOrBlocked:
                                                                    UDF_getSetSize(UDF_getSetUnion(attackData.adjSquaresAttackedPostMove, nextToKing.adjBlockedSquares))
                                                            } As theKingChecked,

                                                            If(gblPF_DEBUG_EVAL,
                                                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                    " fCE_900(): heuristic16 preMove" & 
                                                                    " # of adj Squares: " & UDF_getSetSize(nextToKing.adjSquaresSet) &
                                                                    " theKingChecked.preMove: " & theKingChecked.preMove &
                                                                    " theKingChecked.postMove: " & theKingChecked.postMove &
                                                                    " adjSquaresAttackedPreMove: " & attackData.adjSquaresAttackedPreMove &
                                                                    " adjSquaresAttackedPostMove: " & attackData.adjSquaresAttackedPostMove &
                                                                    " numAdjAttackedOrBlocked: " & theKingChecked.numAdjAttackedOrBlocked
                                                                })
                                                            );
                                                            Switch(true,
                                                                (UDF_getSetSize(nextToKing.adjSquaresSet) - UDF_getSetSize(attackData.adjSquaresAttackedPostMove))=1 &&
                                                                (!theKingChecked.postMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_900(): heuristic16 postmove stalemate detected "
                                                                        })
                                                                    );
                                                                    -1000,  // discourage the move
                                                                theNodes.current.inactiveColorMoveRecord.targetSquareName in nextToKing.adjSquaresSet &&
                                                                (
                                                                    attackData.numTargetSquareAttackedByThem>0 || // somebody can get me
                                                                    (attackData.numTargetSquareAttackedByThem=0 && (!attackData.isTargetSquareProtectedByMe)) // king can get me
                                                                ),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_900(): heuristic16 adjacent target square is not safe"
                                                                        })
                                                                    );
                                                                    -50,
                                                                attackData.numTargetSquareAttackedByThem>0,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " fCE_900(): heuristic16 target square is not safe"
                                                                        })
                                                                    );
                                                                    0,

                                                                theKingChecked.numAdjAttackedOrBlocked>=UDF_getSetSize(nextToKing.adjSquaresSet) && theKingChecked.postMove,
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " checkmate detected FEN: " & theNodes.current.preMoveFEN
                                                                        })
                                                                    );
                                                                    10000, // strongly encourage the move
                                                                        
                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove)>UDF_getSetSize(attackData.adjSquaresAttackedPreMove),
                                                                    If(gblPF_DEBUG_EVAL,
                                                                        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                                                                            " detected increase in adj squares attacked BONUS:" &
                                                                            (
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                                UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                            ) * 21
                                                                        })
                                                                    );
                                                                    (
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPostMove) - 
                                                                        UDF_getSetSize(attackData.adjSquaresAttackedPreMove)
                                                                    ) * 21,
                                                                // default
                                                                0
                                                            )
                                                        )
                                                    )
                                                )
                                            ),

                                            // Sum: TBD...
                                            0
                                        )
                                } As totalBonus,

                                If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,totalBonus.totalBonus,-totalBonus.totalBonus)
                            )
                        ),

                    heuristic18:
                        // add some order
                        If(UDF_FEN_TO_ACTIVE_COLOR(theNodes.current.preMoveFEN)=WHITE,
                            // THEN
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_WHITE, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "128",
                                                    RandBetween(0,15),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(15,35)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(30,45),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(10,25),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(0,15),

                                        // default
                                        0
                                    ),

                                // default
                                0
                            ),

                            // ELSE
                            Switch(theData.evalData.gamePhasePreMove,
                                GAME_PHASE_OPENING,
                                    With(
                                        {
                                            inBook:    LookUp(Split(OPENING_BOOK_BLACK, " "),Value=theNodes.current.inactiveColorMoveRecord.san)
                                        } As theOpening,
                                        If(IsBlank(theOpening.inBook),
                                            // THEN - not a book move
                                            Switch(true,
                                                theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "BNP" &&
                                                Text(theNodes.current.inactiveColorMoveRecord.moveDirection) in "456",
                                                    RandBetween(-15,0),
                                                // default
                                                0
                                            ),
                                            // ELSE - the move is in the book
                                            RandBetween(-35,-15)
                                        )
                                    ),
                                GAME_PHASE_MIDDLE,
                                    Switch(true,
                                        // encourage a castle
                                        IsMatch(theNodes.current.inactiveColorMoveRecord.san, "O-O", MatchOptions.BeginsWith),
                                            RandBetween(-45,-30),
                                        // encourage a move out of starting position
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QRBN" &&
                                        UDF_isPieceinStartingPosition(theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier, theNodes.current.inactiveColorMoveRecord.sourceSquareNum),
                                            RandBetween(-25,-10),
                                        theNodes.current.inactiveColorMoveRecord.sourceSquareOccupier in "QBNP" &&
                                        (IsBlank(theNodes.current.inactiveColorMoveRecord.captureSquareOccupier) || theNodes.current.inactiveColorMoveRecord.captureSquareOccupier=EMPTY_FILLER_ABBR), // don't randomly step into a bad capture
                                            RandBetween(-15,0),
                                        
                                        // default
                                        0
                                    ),

                                // default
                                0
                            )
                        ),
                    heuristic19:
                        RandBetween(-8,8)

                })
        });

        UpdateIf(colPF_MOVE_TREE, nodeID=pNodeID, {
            postMoveEvalRecord: 
                Patch(LookUp(colPF_MOVE_TREE, nodeID=pNodeID).postMoveEvalRecord, {
                    centipawn:
                        With(
                            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNodeUpdated,
                            
                            If(gblPF_DEBUG_EVAL,
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic11: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic11
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic12: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic12
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic15: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic15
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic16: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic16
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic18: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic18
                                });
                                Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
                                    " heuristic19 the God of Randomness BONUS/PENALTY sub-total: " & theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                                })
                            );
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic11 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic12 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic15 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic16 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic18 +
                            theCurrentNodeUpdated.postMoveEvalRecord.heuristic19
                        )
                })
        })
    );

    If(gblPF_DEBUG_EVAL,
        With(
            LookUp(colPF_MOVE_TREE, nodeID=pNodeID) As theCurrentNode,

            Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & 
                " fCE_900(): san: " & theCurrentNode.inactiveColorMoveRecord.san &
                " pacn: " & theCurrentNode.inactiveColorMoveRecord.pacn &
                " centipawn: " & theCurrentNode.postMoveEvalRecord.centipawn
            })
        )
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") & " fCE_900(): exiting"})
)

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_900_preEvaluate(): entering"
    })
);


With(
    {
        d0Node:         LookUp(colPF_MOVE_TREE, nodeDepth=0),
        dynamicData:    LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME)
    } As theData,

    UpdateIf(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME, {
        evalData:   Patch(LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME).evalData, {
                        theirHangingSquaresSet: UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_INACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        myHangingSquaresSet:    UDF_getHangingSquareNamesSet(theData.d0Node.postMoveBoard, UDF_FEN_TO_ACTIVE_COLOR(theData.d0Node.postMoveFEN)),
                        gamePhasePreMove:       comChessEngineFunctions.fME_getGamePhase(theData.d0Node.postMoveFEN) // D0 post move is D1 pre move...
                    })
    })
);

If(gblPF_DEBUG_EVAL,
    With(
        LookUp(colPF_DYNAMIC_DATA, dataName=DYNAMIC_DATA_PF_NAME) As theData,

        Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
            " comChessEngineFunctions.fCE_900_preEvaluate(): DEBUG" &
            " myHangingSquaresSet: " & theData.evalData.myHangingSquaresSet &
            " theirHangingSquaresSet: " & theData.evalData.theirHangingSquaresSet
        })
    )
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_900_preEvaluate(): exiting"
    })
);

If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_900_postEvaluate(): entered"
    })
);





If(gblPF_DEBUG_EVAL,
    Collect(colPF_logs, {logOffset: GET_LOG_INDEX, logEntry: Text(Now(), "mm/dd/yyyy hh:mm:ss.fff") &
        " comChessEngineFunctions.fCE_900_postEvaluate(): exiting"
    })
);
